::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #152128;
}

::-webkit-scrollbar-thumb {
    background: #37474F;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}

::-webkit-scrollbar-corner {
    background: #000;
}

body {
    background-color: #152128;
    margin-left: 0;
    margin-right: 0;
}

a {
    color: #9E9E9E;
}

a:hover {
    color: #B0BEC5 !important;
}

a.headerlink:hover {
	background: none;
}

tt {
    background: none;
}

pre {
    background: #0f181d;
    color: #B0BEC5;
}

select, input {
    border-color: #152128;
    background: #0f181d;
    color: #a7b5bc;
}

input[type="text"], div.sphinxsidebar input {
    background-color: #030e13;
    border: 1px solid #6c828d;
    border-radius: 3px;
    line-height: 1.3em;    
    font-size: 1em;
    padding: 2px 5px;
}

input[type="submit"] {
    border: 1px solid #6c828d;
    font-size: 1em;
    line-height: 1.3em;
    background-color: #455A64 !important;
    border-radius: 3px;
    padding: 2px 5px;
}

dt:target, .highlight, .highlighted {
    background-color: rgba(141, 160, 35, 0.22);
    color: #EEE;
}

code {
    background-color: inherit !important;
}

div.document {
    background: transparent;
}

div.body {
    background: #263238;
    color: #78909C;
    padding: 20px 15px 30px;
    margin-left: 0;
}

div.body dl {
    border-bottom: 1px solid #1d2b31;
}

div.body img {
    -webkit-filter: invert(1);
    filter: invert(1);
}

div.body a {
    color: #CFD8DC;
}

div.body a:visited {
    color: #B0BEC5;
}

div.body a:hover {
    color: #ECEFF1;
}

div.body hr {
    border: 0;
    background-color: #78909C;
    height: 1px;
}

div.body pre {
    background-color: #0f181d;
    border-color: #455A64;
}

div.body dt {
    color: #78909C;
    background: #172227;
    padding: 4px;
    margin: .5em;
    border-bottom: 2px solid #455A64;
}

div.body h1, div.body h2, div.body h3, div.body h4, div.body h5, div.body h6 {
    border-bottom: 1px solid #637983;
    margin: 0;
    background: none;
    color: #699bad;    
}

div.sphinxsidebarwrapper {
    /* position: fixed; */
    float: left;
    padding: 10px 5px 0 10px;
    width: 215px;
    overflow-y: auto;
    overflow-x: hidden;
    height: 100%;
    margin-top: -4em;
}

div.sphinxsidebarwrapper a {
    color: #607D8B;
}

div.sphinxsidebar {
    background-color: transparent;
}

div.sphinxsidebar p {
    color: #546E7A;
}

div.sphinxsidebar h2, div.sphinxsidebar h3, div.sphinxsidebar h3 a, div.sphinxsidebar h4, div.sphinxsidebar h5, div.sphinxsidebar h6 {
    color: #B0BEC5;
}

div.sphinxsidebar ul {
    color: #37474F;
}

div#searchbox {
    background: #0c171d;
    color: #fff;
    padding: .5em;
    border-radius: 5px;
    border: 1px solid #223742;
}

div#searchbox h3 {
    margin-top: 10px;
}

div.sphinxsidebar #searchbox input[type="text"] {
    width: 132px;
}

div.sphinxsidebar #searchbox input[type="submit"] {
    width: 34px;
}

div#sidebarbutton {
    margin-left: 280px;
    background-color: #0c171d !important;
}
div#sidebarbutton span {
    color: #90A4AE;
}

div.sidebar {
    border: 1px solid #78909C;
    background-color: #37474F;
    border-radius: 5px;
}

div.body tt.descname, div.body code.descname {
    color: #81a1b1;
}

div.body dt em {
    color: #538ead;
}

code.descname {
    color: #81a1b1;
}

div.related {
    float: right;
    background-color: #455A64;
    color: #B0BEC5;
    padding: 0.5em 0;
    border-top: 1px solid #444;
    margin: 0;
    min-height: 2em;
    border: 0;
    width: calc(100% - 230px);
    line-height: 40px;
}

div.related:first-child {
    border-top: 0;
    border-bottom: 1px solid #000;
}

div.related a {
    color: #ECEFF1;
}

div.related .right {
    color: #455A64;
    font-size:0;
}

div.related .right a {
    color: #78909C;
    background: #263238;
    padding: 5px;
    font-size:15px !important;
    border-radius: 5px;
    border: 1px solid #90A4AE;
}

div.related .right a:hover {
    text-decoration: none;
    color: #0c171d !important;
    background-color: #ecf0f3;
}

div.related ul li {
    float: left;
    padding: 0 4px 0 0;
}

div.related ul .right {
    float: right;
    padding: 0;
}

div.related img {
    margin-top: .75em !important;
}

div.topic {
    background-color: #152128;
    border: 1px solid #90A4AE;
}

.highlight .go /*output*/
{
    color: #AAA;
}

.highlight .gt, .highlight .nf /*tracebacks*/
{
    color: #4772D1;
}

.highlight .nd {
    color: #aaa;
}

table.indextable tr.cap {
    margin-top: 10px;
    background-color: #1d2b31;
}

table.docutils thead tr {
    background-color: #37474F;
}

table.docutils tbody tr.row-even {
    background-color: #0b191f;
}

table.docutils tbody tr.row-odd {
    background-color: #010a0e;
}

table.docutils td, table.docutils th {
    background-color: inherit;
    border: 1px solid #455A64 !important;
    border-radius: 3px;
}

table.docutils th {
    color: #B0BEC5;
}

div.note {
    background-color: #2d3f48;
    color: #aec6d2;
    border-color: #607D8B;
}

div.warning {
    border-color: #803737;
    color: #ab6530;
    background-color: #332418;
}

div.seealso {
    color: #b9b278;
    background-color: rgba(72, 66, 18, 0.31);
    border: 1px solid #a19631 !important;
}

div.seealso dd {
    margin-left: 10px;
}

div.seealso dt {
    color: #b5b49f;
    background: #1d211b;
    border-bottom: 2px solid #6b654d;
}

div.deprecated, div.deprecated-removed {
    background-color: rgba(28, 3, 0, 0.48);
    color: #923d3d;
    padding: .1em .5em;
    border-radius: 5px;
    border: 1px solid #752e2e;
    margin-top: .5em;
}

div.seealso ul {
    margin-right: 1em;
}

div.modindex-jumpbox {
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
}

#search-results span.pre {
    color: #546E7A;
}

ul.search li div.context {
    color: #90A4AE;
}

.refcount {
    color: #76b976;
}

div.footer {
    float: right;
    line-height: 150%;
    margin-top: 0;
    text-align: right;
    margin-right: 10px;
    color: #607D8B;
}

div.footer a {
    color: #78909C;
}

