/* Hide some things I dislike seeing */

/* Very simple ad hider */
[class="ad"],
[class *="ads"],
[class ^="ad-"],
[class ^="ad_"],
[class *="-ad-"],
[class $="-ad"],
[class $="_ad"],
[id *="_ad"]{
    display: none;
}
#ad-container {
    display: none;
}
[id="#ad-container"] {
    display: none;
}

/* Very simple 'nocontent' hider */
[class *="nocontent"],
.hidden {
    display: none;
}
.met-rule-660 p
/* Very simple popup hider */
[class *="popup"],
[class *="met-flyout met-rule-660"] {
    display: none;
}

