html body #global-wrapper #footer-push {
	height: 50px;
}

/*removes annoying follows-u-sidebar ad*/
#content #siderail {
	display: none !important;
}
#global-wrapper.with-siderail #content #bodyContent {
	width: 100%;
}

/*tries to remove ad rails for those without blockers*/
#content div#atflb,
#content div#btflb {
	display: none !important;
}
/*optional dark theme*/
#content img {
	background: transparent !important;
	-webkit-filter: drop-shadow(0px 0px 2px rgba(255,255,255,1));
	-moz-filter: drop-shadow(0px 0px 2px rgba(255,255,255,1));
	filter: drop-shadow(0px 0px 2px rgba(255,255,255,1));
}
#content *, div#content {
    background: #262626 !important;
    border-color: #585858 !important;
    border-width: 2px !important;
    color: #C2C2C2 !important;
}

body > table {
    background: #222;
}
body > table td {
    background: #444;
    color: #DDD;
}
body > table form table td {
    background: #555;
    color: #DDD;
}
body > table form table td a:visited {
    color: #000;
}
body > table form table td a:link {
    color: #BBB;
}
body > table form table td b font {
    color: #00F;
}
body > table table td font[color="black"] {
    color: #DDD;
}

body > table center font font[color="green"] {
    color: #0D0;
}

:root {
