	--round1: 50px;  /* Chat Avatar roundness 50% Circle. */
  --round2: 50px;  
	--margin1: 0px; /* Chat avatar Margin left/right */
	--margin2: 0px; /*Chat avatar Margin top/bottom */
	--compact: 15px;    /* Theme Default 15px, Discord Default 20px. This makes the chat msgs more compact. */

